from enum import Enum
from os import environ


class Length(Enum):
    TOKEN = int(environ.get('TOKEN_LENGTH', 20))


class APP(Enum):
    APP_NAME = environ.get('PROJECT_NAME', 'Basic Auth APP')
    APP_DESCRIPTION = environ.get('PROJECT_DESCRIPTION', 'App with basic auth process, and button counters')
    APP_LOGO_B64 = environ.get('PROJECT_LOGO', 'data:image/svg+xml;utf8;base64,')
